  const [onSubmitClick,setOnSubmitClick]=useState(false)
    const [previousstate, setColor] = useState();
    const [pfromPlace,fromPlace]=useState('');
    const [ptoPlace,toPlace]=useState('');

    const handleSubmit = event => {
      console.log('handleSubmit ran');
      event.preventDefault(); 

      console.log('pfromPlace ', fromPlace);
      console.log('ptoPlace ', toPlace);

      fromPlace('');
      toPlace('');
    };

    console.log('value123456',onSubmitClick)
  return (
    <><div className="Welcome">
        <h2 className="Welcome-header">
          Welcome to Bus booking Website.
        </h2>
        <h3>
          Places
        </h3>
        <form className='SubmitPlaces' onSubmit={()=>setOnSubmitClick(!onSubmitClick)}>
        <p className='PlaceLst'>
          <b>from</b>
          <input
          id="fromPlace"
          name="fromPlace"
          type="text"
          onChange={event => fromPlace(event.target.value)}
          value={pfromPlace}
        />

          <b>to   </b>    
          <input
          id="toPlace"
          name="toPlace"
          type="text"
          onChange={event => toPlace(event.target.value)}
          value={ptoPlace}
        />

        </p>
          <button type='submit'> Search bus</button>
        </form>
    </div>
    <div>
      {onSubmitClick && <BusInfo/>}
    </div>
</>

  );
}

export default App;
